function WolfFas(hero,enemy)
    local data=GetUnitData(hero)
    local a,x,y=GetUnitFacing(hero),GetUnitXY(hero)
    local xn,yn=MoveXY(x,y,150,a)
    local unit=CreateUnit(GetOwningPlayer(hero),FourCC("nwlt"),xn,yn,a)
    SetUnitVertexColor(unit,255,255,255,128)
    SetUnitPathing(unit,false)
    --UnitAddAbility(unit,FourCC("Aloc"))
    SetUnitMoveSpeed(unit,999)
    UnitApplyTimedLife(unit, FourCC('BTLF'), 5)
    IssueTargetOrder(unit,"attack",enemy)
    BlzSetUnitBaseDamage(unit,data.WolfPerAttack, 0)
    AddMaxLife(data.SpiritWolf, data.HPForSummon)
end
