function AddGold(data,amount)
    if not data.gold then
        data.gold=0
    end
    data.gold=data.gold+amount
    BlzFrameSetText(data.GoldTextFH,R2I(data.gold))
end

function RewardGoldForKill(hero)
    UnitAddGold(hero,1)
end