                data.isSpined = true
                --print("start spin")
                StartAndReleaseSpin(data)
                if not data.tasks[2] then
                    data.tasks[2] = true
                    --print("Первый раз прокрутился")
                end
            end
        else
            data.ChargingAttack = 0
            data.isSpined = false
        end

